+++ b/lib/krb5.c
@@ -138,15 +138,12 @@ krb5_decode(void *app_data, void *buf, int len,
   (void)conn;

   enc.value = buf;
   enc.length = len;
   maj = gss_unwrap(&min, *context, &enc, &dec, NULL, NULL);
-  if(maj != GSS_S_COMPLETE) {
-    if(len >= 4)
-      strcpy(buf, "599 ");
+  if(maj != GSS_S_COMPLETE)
     return -1;
-  }

   memcpy(buf, dec.value, dec.length);
   len = curlx_uztosi(dec.length);
   gss_release_buffer(&min, &dec);

@@ -523,10 +520,12 @@ static CURLcode read_data(struct connectdata *conn,
   if(result)
     return result;
   buf->size = conn->mech->decode(conn->app_data, buf->data, len,
                                  conn->data_prot, conn);
